mapper.update:
update cart set
usersid=#{usersid} , bookid=#{bookid} , num=#{num} ,
price=#{price}
where cartid=#{cartid}

mapper.select:
select a.* , b.username , c.bookname , c.image from cart a , users b ,
book c
where 1=1 and a.usersid = b.usersid and a.bookid = c.bookid

	and a.usersid = #{usersid}


	and a.bookid = #{bookid}


	and a.num = #{num}


	and a.price = #{price}
